import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { Table, Tag, Space } from 'antd';


const Home = () => {
    const [users, setUser] = useState([]);

    useEffect(() => {
        loadUsers();
    }, []);
    useEffect(() => {
        // document.getElementById("myTbody")[0].pageMe({ pagerSelector: '#myPager', showPrevNext: true, hidePageNumbers: false, perPage: 4 });
    })

    const loadUsers = async () => {
        const result = await axios.get("http://localhost:3004/users");
        setUser(result.data);
    };


    // const onDeleteYes = async id => {
    //     await axios.delete(`http://localhost:3004/users/${id}`);
    //     loadUsers();
    // };

    // const onDeleteNo = async () => {
    //     const result = await axios.get("http://localhost:3004/users");
    //     setUser(result.data);
    // }

    const deleteUser = async id => {
        await axios.delete(`http://localhost:3004/users/${id}`);
        loadUsers();
    };

    const columns = [
        {
            title: '#',
            dataIndex: '#',
            key: '#',

        },
        {
            title: 'Name',
            dataIndex: 'name',
            key: 'name',

        },
        {
            title: 'Email',
            dataIndex: 'email',
            key: 'email',

        },
        {
            title: 'City',
            dataIndex: 'city',
            key: 'city',

        },
        {
            title: 'Action',
            dataIndex: 'action',
            key: 'action',
            render: () => (
                <a>Add</a>,
                <a>Delete</a>
            ),
        },

    ]
    return (
        <>
            <Table columns={columns} dataSource={result.data} />

        </>
    );
};

export default Home;
