import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import Modal from "react-modal";


const Home = () => {
    const [users, setUser] = useState([]);

    useEffect(() => {
        loadUsers();
    }, []);

    const loadUsers = async () => {
        const result = await axios.get("http://localhost:3004/users");
        setUser(result.data.reverse());
    };


    


    const onDeleteYes = async id => {
        await axios.delete(`http://localhost:3004/users/${id}`);
        loadUsers();
    };

    const onDeleteNo = async () => {
        const result = await axios.get("http://localhost:3004/users");
        setUser(result.data);
    }


    // const deleteUser = async id => {
    //     await axios.delete(`http://localhost:3004/users/${id}`);
    //     loadUsers();
    // };

    return (
        <div className="container">
            <div className="py-4">
                <h1 className="mb-9">Users Information Table</h1>
                <table class="table border shadow">
                    <thead class="thead-dark">
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Name</th>
                            <th scope="col">Email</th>
                            <th scope="col">City</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map((user, index) => (
                            <tr>
                                <th scope="row">{index + 1}</th>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td>{user.city}</td>

                                <td>
                                    <Link class="btn btn-primary mr-2" to={`/users/${user.id}`}>
                                        View
                                    </Link>
                                    <Link class="btn btn-outline-primary mr-2"
                                        to={`/users/edit/${user.id}`}  >
                                        Edit
                                    </Link>
                                    <Link
                                        class="btn btn-danger"
                                        onClick={() => modalIsOpen(true)} >
                                        Delete
                                    </Link>
                                    <div class="modal" tabindex="-1">
                                        <div class="modal-dialog">
                                            <div class="modal-content">
                                                <div class="modal-header">
                                                    <h5 class="modal-title">Warning!</h5>
                                                    <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                                                </div>
                                                <div class="modal-body">
                                                    <p>Are you sure you want to delete this user?</p>
                                                </div>
                                                <div class="modal-footer">
                                                    <Link type="button" class="btn btn-success" to={`/`} data-bs-dismiss="modal">No</Link>
                                                    <Link type="button" class="btn btn-danger" onClick="onDeleteYes">Yes</Link>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>

            </div>
        </div>
    );
};

export default Home;
