import React, { useState } from "react";
import axios from 'axios'
import { Link, useHistory } from "react-router-dom";
import { data, id, users } from "jquery";

const AddUser = () => {
    let history = useHistory();
    const [user, setUser] = useState({
        name: "",
        email: "",
        city: "",
        phone: "",
    });
    const [match, setMatch] = useState(false)
    const { name, email, city, phone } = user;
    const onInputChange = e => {
        setUser({ ...user, [e.target.name]: e.target.value });
    };




    const onSubmitUser = async e => {
        e.preventDefault();

        let matching = false

        const result = await axios.get("http://localhost:3004/users");

        result.data.map((users) => {
            if (users.email == user.email) {
                setMatch(true)
                matching = true
            }
        })

        if (!matching) {
            await axios.post("http://localhost:3004/users", user);
            history.push("/");
        }

    }







    return (
        <div className="container">
            <Link className="btn btn-dark float-left mt-4" to="/">
                Back
      </Link>

            <div className="w-75 mx-auto shadow p-5">
                <h2 className="text-center mb-4">Add A User</h2>
                <form onSubmit={e => onSubmitUser(e)}>
                    <div className="form-group">
                        <input
                            type="text"
                            className="form-control form-control-lg"
                            placeholder="Enter Your Name"
                            name="name"
                            value={name}
                            onChange={e => onInputChange(e)}
                        />
                    </div>
                    <div className="form-group">
                        <div class="alert alert-warning" role="alert" style={{ color: "red", display: match ? "block" : "none" }}>Email present</div>
                        <input
                            type="email"
                            className="form-control form-control-lg"
                            placeholder="Enter Your E-mail Address"
                            name="email"
                            value={email}
                            onChange={e => onInputChange(e)}
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="text"
                            className="form-control form-control-lg"
                            placeholder="Enter Your City"
                            name="city"
                            value={city}
                            onChange={e => onInputChange(e)}
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="text"
                            className="form-control form-control-lg"
                            placeholder="Enter Your Phone Number"
                            name="phone"
                            value={phone}
                            onChange={e => onInputChange(e)}
                        />
                    </div>

                    <button className="btn btn-success btn-block">Add User</button>
                </form>
            </div>
        </div>
    );
}


export default AddUser;
